import { Product } from "@/type/types.ts";
import { server } from "@/utils/server.ts";
import { Card, Rate, Space } from "antd";
import { Link } from "react-router-dom";
import {currencyFormat} from "@/utils/common.ts";

export type ElementDistance = {
  readonly distance: {
    readonly text: string;
    readonly value: number;
  };
  readonly duration: {
    readonly text: string;
    readonly value: number;
  };
};

export interface RowElementDistance {
  elements: ElementDistance[];
}

export type DistanceResponse = {
  destination_addresses: string;
  origin_addresses: string;
  rows: RowElementDistance[];
  status: string;
};

const fallback =
  "data:image/png;base64,";

export function ProductCard({ product }: { product: Product }) {
  return (
    <div className="group">
      {product.minPrice ? (
        <Link to={`/san-pham/${product.slug}`}>
          <Card
            size="small"
            hoverable
            cover={
              <div className="overflow-hidden rounded-t-lg">
                <img
                  alt="example"
                  className="aspect-square object-cover transition-transform duration-300 group-hover:scale-110"
                  src={
                    product.thumbnail ? server + product.thumbnail : fallback
                  }
                />
              </div>
            }
          >
            <Space direction="vertical" size="small" className="flex font-sans">
              <div className="text-left overflow-hidden text-ellipsis  whitespace-nowrap">
                {product.name}
              </div>
              <div>
                <Rate
                  disabled
                  allowHalf
                  value={product.rate}
                  className="text-sm mr-2"
                />
                ({product.rate.toFixed(1)}/{product.rateCount})
              </div>
              <div>
                <div className="text-left text-[20px] nunito">
                  {currencyFormat(product.minPrice)}
                  {product.maxPrice === product.minPrice
                    ? ""
                    : " - " + currencyFormat(product.maxPrice)}
                </div>
                <div>Đã bán: {product.sales}</div>
              </div>
            </Space>
          </Card>
        </Link>
      ) : (
        <Card
          className="!border-gray-400 opacity-20"
          size="small"
          hoverable
          cover={
            <div className="overflow-hidden rounded-t-lg">
              <img
                alt="example"
                className="aspect-square object-cover transition-transform duration-300 group-hover:scale-110"
                src={product.thumbnail ? server + product.thumbnail : fallback}
              />
            </div>
          }
        >
          <Space direction="vertical" size="small" className="flex">
            <div className="text-left overflow-hidden text-ellipsis whitespace-nowrap">
              {product.name}
            </div>
            <div>
              <Rate
                disabled
                allowHalf
                value={product.rate}
                className="text-sm mr-2"
              />
              ({product.rate.toFixed(1)}/0)
            </div>
            <div className="flex justify-between items-center">
              <div className="text-left text-[20px] nunito">Hết hàng</div>
            </div>
          </Space>
        </Card>
      )}
    </div>
  );
}
